import React, {useRef, useEffect, useState} from "react";
import * as d3 from 'd3';


    function Search(){
      const [data, setData] = useState([]);
      useEffect(()=> {
         d3.csv("/data/video_games.csv")
            .then((da) =>{
               setData(da);
            })
         }, 
         []
      );
      const [name, setName] = useState('');
      const [foundGames, setFoundGames] = useState(data)

      const filter = (e) => {
         const keyword = e.target.value;
         if(keyword !== ''){
            const results = data.filter((game) =>{
               return game.Title.toLowerCase().startsWith(keyword.toLowerCase());
            });
            setFoundGames(results);
         }else{
            setFoundGames(data);
         }
         setName(keyword);
      };

    return (
      <React.Fragment>
         <div className="container">
            <input
               type="search"
               value={name}
               onChange={filter}
               className="input"
               placeholder="Search for a Game"
            />
            <div className="game-list">
               {foundGames && foundGames.length > 0 && foundGames.length < 10 ?(
                  foundGames.map((game) => (
                     <li key={game.id} className="game">
                        <span className="game-title">{game.Title}</span>
                        <span className="game-platform">{game.Platform}</span>
                     </li>
                  ))
               ):(
                  <h1>No results found</h1>
               )}
            </div>
         </div>
      </React.Fragment>
    );
   }

export default Search;
